CREATE TABLE skinny_activation (id BIGINT AUTO_INCREMENT, hash VARCHAR(32) NOT NULL, user_id INT, INDEX hash_idx (hash), INDEX user_hash_idx (user_id, hash), INDEX user_id_idx (user_id), PRIMARY KEY(id)) ENGINE = INNODB;
CREATE TABLE skinny_check (user_id INT, item_id BIGINT, PRIMARY KEY(user_id, item_id)) ENGINE = INNODB;
CREATE TABLE skinny_item (id BIGINT AUTO_INCREMENT, name VARCHAR(255), text TEXT, list_id BIGINT NOT NULL, created_at DATETIME NOT NULL, updated_at DATETIME NOT NULL, slug VARCHAR(255), position BIGINT UNSIGNED, UNIQUE INDEX name_slug_idx (slug), UNIQUE INDEX item_sortable_idx (position), INDEX list_id_idx (list_id), PRIMARY KEY(id)) ENGINE = INNODB;
CREATE TABLE skinny_list (id BIGINT AUTO_INCREMENT, name VARCHAR(255) NOT NULL, private TINYINT(1) DEFAULT '0' NOT NULL, user_id INT, description TEXT, created_at DATETIME NOT NULL, updated_at DATETIME NOT NULL, slug VARCHAR(255), UNIQUE INDEX namer_slug_idx (slug), INDEX user_id_idx (user_id), PRIMARY KEY(id)) ENGINE = INNODB;
